mapper.select:
SELECT ag.activity_id
FROM activity_goods ag
         INNER JOIN activity a ON ag.activity_id = a.activity_id
WHERE ag.goods_id = #{goodsId}
  AND ag.source = #{source}
  AND ag.channel = #{channel}
  AND a.status = 'ACTIVE'
  AND a.start_time <= NOW()
  AND a.end_time >= NOW()
LIMIT 1

SELECT *
FROM activity_goods
WHERE activity_id = #{activityId}
  AND goods_id = #{goodsId}
  AND source = #{source}
  AND channel = #{channel}
LIMIT 1
